            type: 'POST',
            data: $(this).serialize(), // Сериализуем данные формы
            success: function (response) {
                // Обработка ответа от сервера
                // response содержит ответ сервера

                // Пример обновления элемента на странице с информацией о результате
                if (response.success) {
                    location.reload();
                } else {
                    $('#resultMessage').text(response.message);
                }
            }
        });
    });

    $("#FinalTestGo").click(function (){
        window.location.href = "main_test/main_test.php"
    })
</script>
